import React from 'react'

export function ErrorsBox(props) {
  const { errors } = props

  const errorMessages = Object.values(errors).map(error => error.message)

  return <>
    <div
      className='text-red'
      style={{
        minHeight: errorMessages.length > 0 ? 24 : 0
      }}
    >
      {errorMessages.map(errorMsg => errorMsg)}
    </div>
  </>
}
